// https://leetcode.cn/problems/substring-with-concatenation-of-all-words/description/

class Solution {
public:
    vector<int> findSubstring(string s, vector<string>& words) {
        vector<int> ans;

        if(s == "") {
            for(int i = 0; i <= s.size() - words.size(); i++)
            {
                ans.push_back(i);
            }
            return ans;
        }

        int expectedLen = words.size() * words[0].size();

        if(s.size() < expectedLen) return ans;

        for(int i = 0; i <= s.size() - expectedLen; i++)
        {
            bool isValid = true;
            string tmp = s.substr(i, expectedLen);
            for(auto& word : words) {
                int idx = 0;
                for(idx; idx < tmp.size(); idx += word.size())
                {
                    string tmp2 = tmp.substr(idx, word.size());
                    if(tmp2 == word) break;
                }

                if(idx == tmp.size()) {
                    isValid = false;
                    break;
                }
                else {
                    tmp.erase(idx, word.size());
                }
            }     

            if(isValid) ans.push_back(i);
        }

        return ans;
    }
};